def on_message(client, userdata, message):
    global rookhok_temp
    global rookhok_hum

    if 'smokeroom_temperature' in message.topic:
        rookhok_temp = message.payload.decode('utf-8')
    elif 'smokeroom_humidity' in message.topic:
        rookhok_hum = message.payload.decode('utf-8')
    else:
        print('huh?', message.topic, message.payload)

