def thrd():
    global text_canvas
    global text_draw
    global text_width
    global t

    sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    sock.bind(('0.0.0.0', 5020))

    while True:
        try:
            data, sender_addr = sock.recvfrom(1024)
            print('message from %r: %r' % (sender_addr, data))
            text = data.decode('utf-8')

            text_width, text_height = fontbig.getsize(text)
            text_width += 64
            text_canvas = Image.new('1', (text_width, text_height))
            text_draw = ImageDraw.Draw(text_canvas)
            text_draw.text((0, 0), text, fill=(1), font=fontbig)

            t = time.time()

        except Exception as e:
            print('exception: ', e)

def put_frame(data):
    global sock

    MESSAGE = bytearray()

    for d in range(0, 3):
        for x in range(0, 64, 8):
            for y in range(0, 8):
                yy = 7 - y + d * 8

                xyo = yy * 64 + x

                b = 0
                for o in range(xyo + 7, xyo - 1, -1):
                    b <<= 1
                    b |= data[o]

                MESSAGE.append(b)

    data_lzjb = compress(MESSAGE)
    sock.sendto(data_lzjb, (UDP_IP, UDP_PORT))

